export const initialState = {
  isLoggedIn: false,
  user: null,
  signUpdate: {},
  loginData: {},
};

export const loginAction = (data) => ({
  type: "LOGIN",
  data,
});
export const logoutAction = (data) => ({
  type: "LOGOUT",
});

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOGIN":
      return {
        ...state,
        isLoggedIn: true,
        user: action.data,
      };
    case "LOGOUT":
      return {
        ...state,
        isLoggedIn: false,
        user: null,
      };
    default:
      return state;
  }
};

export default reducer;
